INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (15, 103, '不仅要提升政务服务水平，更要打造一流营商环境', '打造一流营商环境', 3, '网络', '不断深化改革，优化营商环境', '/profile/upload/2024/09/16/2211_20240916213026A001.jpg', '<p>　“通过党建+模式的探索，不仅要提升政务服务水平，更要打造一流营商环境。”行政审批局相关负责人表示。近年来，行政审批局不断在简化流程、优化服务，方便群众办事上下功夫。设立“红色代办”党员服务队，选拔业务骨干帮助企业解决从项目审批到开工生产的全流程问题。组建“我来跑”志愿服务队，开展镇村联动的代办服务。除此以外，相关部门还对企业和群众“高频”事项推行“定制套餐”服务，办事材料精简20%，环节减少20%，时限压缩30%。</p><p style=\"text-align: center; \"><br></p><p>　　与此同时，不断深化改革，优化营商环境。推动政务服务向村级延伸。建成村级便民服务中心和全科社工队伍，推动74个服务事项下沉至村，形成镇村两级“一窗通办”互联互通，真正实现“多点可办、少跑快办”。据统计，自该镇行政审批局运行以来，日均接待办事群众328人，日均办结事项513件，有效提升群众获得感和幸福感。</p>', '0', '', 10, '1', '1', '0', '1', '1', '0', '1', NULL, '同意', 'pcs', '2024-05-12 16:47:44', 'ry', '2024-09-17 20:44:21');
INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (16, 100, '青春需要磨砺，只有经历过风雨的洗礼，才能绽放出更加绚烂的光彩', '6s', 3, '办公室', 'fgd', '', '<p>　　军训也让我深刻体会到了团队的力量。在队列训练中，我们每个人都是团队的一员，只有大家齐心协力，才能走出整齐的步伐。拔河比赛中，我们更是团结一致，为了班级的荣誉拼尽全力。在这个过程中，我学会了与他人合作，学会了关心和帮助他人。我相信，这份团队精神将在今后的大学生活中发挥重要作用。</p><p><br></p><p>　　军训虽然辛苦，但它也是一段美好的回忆。那些与同学们一起挥洒汗水的日子，那些在烈日下的坚持，都将成为我人生中宝贵的财富。它让我明白了，青春需要磨砺，只有经历过风雨的洗礼，才能绽放出更加绚烂的光彩。</p>', '0', '', 2, '0', '0', '0', '1', '1', '0', '1', NULL, '', 'admin', '2024-05-12 16:53:25', 'admin', '2024-09-17 21:48:37');
INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (17, 103, '《上甘岭》插曲《我的祖国》，经典红歌，振奋人心！', '属地发', 2, '属地发', '属地发', '/profile/upload/2024/09/17/101_20240917192032A001.jpg', '<p style=\"text-align: center; \">&nbsp;&nbsp;</p><p style=\"text-align: center;\">一条大河波浪宽&nbsp;&nbsp;</p><p style=\"text-align: center;\">风吹稻花香两岸&nbsp;&nbsp;</p><p style=\"text-align: center;\">我家就在岸上住&nbsp;&nbsp;</p><p style=\"text-align: center;\">听惯了艄公的号子&nbsp;&nbsp;</p><p style=\"text-align: center;\">看惯了船上的白帆&nbsp;&nbsp;</p><p style=\"text-align: center;\">一条大河波浪宽&nbsp;&nbsp;</p><p style=\"text-align: center;\">风吹稻花香两岸&nbsp;&nbsp;</p><p style=\"text-align: center;\">我家就在岸上住&nbsp;&nbsp;</p><p style=\"text-align: center;\">听惯了艄公的号子&nbsp;&nbsp;</p><p style=\"text-align: center;\">看惯了船上的白帆&nbsp;&nbsp;</p><p style=\"text-align: center;\">这是美丽的祖国&nbsp;&nbsp;</p><p style=\"text-align: center;\">是我生长的地方&nbsp;&nbsp;</p><p style=\"text-align: center;\">在这片辽阔的土地上&nbsp;&nbsp;</p><p style=\"text-align: center;\">到处都有明媚的风光&nbsp;&nbsp;</p><p style=\"text-align: center;\">姑娘好像花儿一样&nbsp;&nbsp;</p><p style=\"text-align: center;\">小伙儿心胸多宽广&nbsp;&nbsp;</p><p style=\"text-align: center;\">为了开辟新天地&nbsp;&nbsp;</p><p style=\"text-align: center;\">唤醒了沉睡的高山&nbsp;&nbsp;</p><p style=\"text-align: center;\">让那河流改变了模样&nbsp;&nbsp;</p><p style=\"text-align: center;\">这是英雄的祖国&nbsp;&nbsp;</p><p style=\"text-align: center;\">是我生长的地方&nbsp;&nbsp;</p><p style=\"text-align: center;\">在这片古老的土地上&nbsp;&nbsp;</p><p style=\"text-align: center;\">到处都有青春的力量&nbsp;&nbsp;</p><p style=\"text-align: center;\">好山好水好地方&nbsp;&nbsp;</p><p style=\"text-align: center; \">条条大路都宽敞&nbsp;&nbsp;</p><p style=\"text-align: center;\">朋友来了有好酒&nbsp;&nbsp;</p><p style=\"text-align: center;\">若是那豺狼来了&nbsp;&nbsp;</p><p style=\"text-align: center;\">迎接它的有猎枪&nbsp;&nbsp;</p><p style=\"text-align: center;\">这是强大的祖国&nbsp;&nbsp;</p><p style=\"text-align: center;\">是我生长的地方&nbsp;&nbsp;</p><p style=\"text-align: center;\">在这片温暖的土地上&nbsp;&nbsp;</p><p style=\"text-align: center; \">到处都有和平的阳光</p>', '0', '', 7, '1', '0', '0', '1', '1', '0', '1', NULL, '同意', 'pcs', '2024-05-12 16:57:56', 'ry', '2024-09-17 19:21:16');
INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (18, 103, '当穿上那身整齐的迷彩服，站在操场上的那一刻，一种使命感油然而生', '公司的风格', 2, '史蒂夫', '史蒂夫', '/profile/upload/2024/09/17/2001_20240917193020A002.png', '<p>　　九月的阳光依旧炽热，怀着对大学生活的憧憬与期待，我迎来了大学的军训。当穿上那身整齐的迷彩服，站在操场上的那一刻，一种使命感油然而生。</p><p><br></p><p>　　军训的日子紧张而充实。每天清晨，在嘹亮的哨声中，我们迅速集合，开始一天的训练。站军姿、踢正步、跑步，每一项训练都考验着我们的耐力和毅力。太阳无情地炙烤着大地，汗水湿透了衣衫，但我们没有一个人退缩。教官们严厉而又细心，他们一遍遍地纠正我们的动作，让我们在不断的重复中逐渐掌握要领。</p><p style=\"text-align: center; \"><img src=\"/profile/upload/2024/09/17/2001_20240917192946A001.png\" style=\"width: 710px;\"><br></p><p>　　在军训中，我不仅锻炼了身体，更磨砺了意志。站军姿时的酸痛、踢正步时的疲惫，都曾让我想过放弃。但每当看到身边的同学们都在坚持，我便告诉自己不能掉队。我学会了在困难面前不低头，勇敢地迎接挑战。这种坚韧不拔的精神，将伴随我走过大学的四年时光。</p><p><br></p><p>　　军训也让我深刻体会到了团队的力量。在队列训练中，我们每个人都是团队的一员，只有大家齐心协力，才能走出整齐的步伐。拔河比赛中，我们更是团结一致，为了班级的荣誉拼尽全力。在这个过程中，我学会了与他人合作，学会了关心和帮助他人。我相信，这份团队精神将在今后的大学生活中发挥重要作用。</p><p><br></p><p>　　军训虽然辛苦，但它也是一段美好的回忆。那些与同学们一起挥洒汗水的日子，那些在烈日下的坚持，都将成为我人生中宝贵的财富。它让我明白了，青春需要磨砺，只有经历过风雨的洗礼，才能绽放出更加绚烂的光彩。</p><p><br></p><p>　　军训结束了，但它带给我的影响将永远留在我心中。我将以更加饱满的热情和坚定的信念，投入到大学生活中去，努力实现自己的梦想。</p>', '0', '', 12, '1', '0', '0', '1', '1', '0', '1', NULL, '同意', 'ks', '2024-05-12 18:45:26', 'ry', '2024-09-17 19:31:08');
