INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (24, 100, '出塞二首·其一：秦时明月汉时关，万里长征人未还', '', 13, '办公室', '', '', '<p style=\"text-align: center;\">秦时明月汉时关，万里长征人未还。&nbsp;&nbsp;</p><p style=\"text-align: center; \">但使龙城飞将在，不教胡马度阴山。</p><p style=\"text-align: center; \"><br></p><p style=\"text-align: center; \"><br></p><p>　　这是一首著名的边塞，表达了诗人希望起任良将，早日平息边塞战事，使人民过上安定的生活的愿望。</p><p><br></p><p>　　诗人从描写景物景入手，首句勾勒出一幅冷月照边关的苍凉景象。“秦时明月汉时关”不能理解为秦时的明月汉代的关。这里是秦、汉、关、月四字交错使用，在修辞上叫“互文见义”，意思是秦汉时的明月，秦汉时的关。诗人暗示，这里的战事自秦汉以来一直未间歇过，突出了时间的久远。次句“万里长征人未还”，“万里”指边塞和内地相距万里，虽属虚指，却突出了空间辽阔。“人未还”使人联想到战争给人带来的灾难，表达了诗人悲愤的情感。</p><p></p>', '0', '', 6, '0', '0', '0', '1', '1', '0', '0', NULL, '同意', 'admin', '2024-05-25 15:05:18', 'ry', '2024-09-16 20:39:52');
INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (25, 100, '僵卧孤村不自哀，尚思为国戍轮台。', '', 13, '办公室', '', '', '<p style=\"text-align: center; \">十一月四日风雨大作（其二）<br></p><p style=\"text-align: center; \"><br></p><p style=\"text-align: center; \">僵卧孤村不自哀，尚思为国戍轮台。&nbsp;&nbsp;</p><p style=\"text-align: center; \">夜阑卧听风吹雨，铁马冰河入梦来。</p><p style=\"text-align: center; \"><br></p><p style=\"text-align: left;\">　　风卷江湖雨暗村，四山声作海涛翻。溪柴火软蛮毡暖，我与狸奴不出门。僵卧孤村不自哀，尚思为国戍轮台。夜阑卧听风吹雨，铁马冰河入梦来。第一首诗主要写十一月四日的大雨和诗人之处境。前两句以夸张之法写大雨瓢泼，其声响之巨，描绘出黑天大风大雨之境，很是生动，波涛汹涌之声正与作者渴望为国出力、光复中原之心相印。后两句转写近处，描写其所处之境，写出作者因天冷而不思出门，其妙处是把作者的主观之感和猫结合一起写。第二首诗以“痴情化门”的手法，深沉地表达了作者收复国土、报效祖国的壮志和那种“年既老而不衰”的矢志不渝精神，向读者展示了诗人的一片赤胆忠心<br></p><p></p>', '0', '', 7, '0', '0', '0', '1', '1', '0', '0', NULL, '同意', 'admin', '2024-05-24 15:05:18', 'ry', '2024-09-17 18:24:58');
INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (26, 100, '王师北定中原日，家祭无忘告乃翁', '', 13, '办公室', '', '', '<h1 style=\"text-align: center; font-size: 20px; margin-top: 0.67em; margin-bottom: 0.67em; color: rgb(51, 51, 51); padding: 0px 5px; font-family: Arial, sans-serif;\">示儿</h1><p style=\"text-align: center; \"><br></p><p style=\"text-align: center; \">死去元知万事空，但悲不见九州同。&nbsp;&nbsp;</p><p style=\"text-align: center; \">王师北定中原日，家祭无忘告乃翁。</p><p style=\"text-align: center; \"><br></p><p>　　是陆游爱国诗中的又一首名篇。陆游一生致力于抗金斗争，一直希望能收复中原。虽然频遇挫折，却仍然未改变初衷。从诗中可以领会到诗人的爱国激情是何等的执着、深沉、热烈、真挚！也凝聚着诗人毕生的心事，诗人始终如一地抱着当时汉民族必然要光复旧物的信念，对抗战事业具有必胜的信心。题目是《示儿》，相当于遗嘱。在短短的篇幅中，诗人披肝沥胆地嘱咐着儿子，无比光明磊落，激动人心！浓浓的爱国之情跃然纸上。</p><p><br></p><p>　　诗人在写此诗的十一年以前，叹息过“死前恨不见中原”，在热烈地期待着旧业的光复。直到临终之际，诗人仍然抱有这样坚定的信念，宋朝的抗敌部队要挥戈</p><p></p>', '0', '', 3, '0', '0', '0', '1', '1', '0', '0', NULL, '同意', 'admin', '2024-05-23 15:05:18', 'ry', '2024-09-17 18:40:21');
