INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (58, 100, '冬虫夏草的主要功效为补肾益肺，止血化痰 ｜ 测试5', '', 12, '办公室', '', '', '<p>是</p>20', '0', '', 2, '1', '0', '0', '0', '0', '0', '0', NULL, '', 'admin', '2024-05-20 15:14:24', 'admin', '2024-09-15 21:05:38');
INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (59, 103, '过零丁洋：人生自古谁无死？留取丹心照汗青。', '公司的风格', 13, '办公室', '收拾收拾', '', '<p style=\"text-align: center;\">辛苦遭逢起一经，干戈寥落四周星。&nbsp;&nbsp;</p><p style=\"text-align: center;\">山河破碎风飘絮，身世浮沉雨打萍。&nbsp;&nbsp;</p><p style=\"text-align: center;\">惶恐滩头说惶恐，零丁洋里叹零丁。&nbsp;&nbsp;</p><p style=\"text-align: center; \">人生自古谁无死？留取丹心照汗青。</p><p>　　回想我早年由科举入仕历尽辛苦，如今战火消歇已熬过了四个年头。国家危在旦夕恰如狂风中的柳絮，个人又哪堪言说似骤雨里的浮萍。惶恐滩的惨败让我至今依然惶恐，零丁洋身陷元虏可叹我孤苦零丁。人生自古以来有谁能够长生不死？我要留一片爱国的丹心映照史册。</p><p><br></p><p>　　首联“辛苦遭逢起一经，干戈寥落四周星。”“起一经”当指天祥二十岁中进士说的，四周星即四年。天祥于德祐元年（1275），起兵勤王，至祥兴元年（1278）被俘，恰为四个年头。此自叙生平，思今忆昔。从时间说，拈出“入世”和“勤王”，一关个人出处，一关国家危亡，两件大事，一片忠心。唐宋时期，一个人要想替国家做出一番事业，必须入仕，要入仕，作为知识分子必须通过科举考选，考选就得读经文天祥遇难时，衣带中留有个自赞文说：“读圣贤书，所学何事，而今而后，庶几无愧”，就是把这两件事拴在一起的。圣人著作就叫经，经是治国安邦的。</p>', '0', '', 29, '0', '0', '0', '1', '1', '0', '0', NULL, '同意', 'pcs', '2024-09-14 15:33:35', 'ry', '2024-09-16 20:40:05');
INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (60, 100, '在炎炎夏日的烈日下，我参加了一次难忘的军训', '', 12, '子部门', '军训还让我认识到了自己的不足之处。在遇到困难时，我曾想过放弃，但最终还是克服了这些消极情绪。我明白了自己的毅力和韧性还需要提高，但这也是一个成长的过程。通过这次军训，我更加明确了自己需要改进的地方，并将继续努力提高自己的能力。', '/profile/upload/2024/09/16/2002_20240916182941A001.jpg', '<p><br></p><p><br></p><p>　　在炎炎夏日的烈日下，我参加了一次难忘的军训。这不仅仅是一次身体上的挑战，更是一次心理和意志的考验。在这短暂而紧张的日子里，我学到了许多宝贵的经验，并对自己的能力有了更深刻的认识。</p><p style=\"text-align: center; \"><img src=\"/profile/upload/2024/09/16/2002_20240916182941A001.jpg\" style=\"width: 710px;\"><br></p><p>　　军训的日子里，我们每天按照严格的日程进行训练。早晨起床、整理宿舍、晨练，紧接着进行一整天的军事技能训练。虽然任务繁重，但正是这种充实的生活让我体验到了军人的严谨和自律。</p><p><br></p><p>　　在训练过程中，我深刻体会到了团队合作的重要性。我们需要与队友紧密配合，共同努力完成任务。此外，我也学会了如何坚持和克服困难。每当遇到挑战时，我告诉自己要坚强、不放弃，只有这样才能取得成功。</p><p><br></p><p>　　军训还让我认识到了自己的不足之处。在遇到困难时，我曾想过放弃，但最终还是克服了这些消极情绪。我明白了自己的毅力和韧性还需要提高，但这也是一个成长的过程。通过这次军训，我更加明确了自己需要改进的地方，并将继续努力提高自己的能力。</p><p><br></p><p>　　总之，这次军训让我深刻体会到了军人精神的内涵。它教会了我如何保持自律、坚定信念，并不断提升自己的能力。在未来的日子里，我将把这些经验教训牢记在心，并不断提高自己的素质和能力。</p>', '0', '', 22, '0', '1', '1', '1', '1', '0', '0', NULL, '', 'admin', '2024-09-16 18:30:17', 'admin', '2024-09-24 19:21:34');
INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (61, 103, '莫等闲、白了少年头，空悲切。', '', 15, '', '', '', '<p>怒发冲冠，凭栏处、潇潇雨歇。抬望眼、仰天长啸，壮怀激烈。三十功名尘与土，八千里路云和月。莫等闲、白了少年头，空悲切。&nbsp;&nbsp;</p><p>靖康耻，犹未雪。臣子恨，何时灭。驾长车，踏破贺兰山缺。壮志饥餐胡虏肉，笑谈渴饮匈奴血。待从头、收拾旧山河，朝天阙。</p><p><br></p><p><br></p><p>　　气得头发竖起，以至于将帽子顶起，登高倚栏杆，一场潇潇细雨刚刚停歇。抬头望眼四望辽阔一片，仰天长声啸叹，一片报国之心充满心怀。三十多年来虽已建立一些功名，但如同尘土微不足道，南北转战八千里，经过多少风云人生。不要虚度年华，花白了少年黑发，只有独自悔恨悲悲切切。靖康年的奇耻，尚未洗雪。臣子愤恨，何时才能泯灭。我要驾着战车向贺兰山进攻，连贺兰山也要踏为平地。我满怀壮志，打仗饿了就吃敌人的肉，谈笑渴了就喝敌人的鲜血。我要从头再来，收复旧日河山，朝拜故都京阙。<br></p>', '0', '', 10, '0', '0', '0', '1', '1', '0', '0', NULL, '同意', 'ks', '2024-09-16 21:10:52', 'ry', '2024-09-16 21:11:36');
INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (62, 100, '满江红·写怀', '', 17, '', '', '/profile/upload/2024/09/16/2004_20240916213558A002.jpg', '<p>　　怒发冲冠，凭栏处、潇潇雨歇。抬望眼、仰天长啸，壮怀激烈。三十功名尘与土，八千里路云和月。莫等闲、白了少年头，空悲切。</p><p><br></p><p>　　靖康耻，犹未雪。臣子恨，何时灭。驾长车，踏破贺兰山缺。壮志饥餐胡虏肉，笑谈渴饮匈奴血。待从头、收拾旧山河，朝天阙。</p>', '0', '', 8, '0', '1', '0', '1', '1', '0', '0', NULL, '', 'admin', '2024-09-16 21:34:59', 'ry', '2024-09-17 14:12:37');
INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (63, 104, '解放战争的三个战略阶段（抗日战争的三个战略阶段）', '公司的风格', 16, '', '', '', '<p>　　大家好，近期很多朋友对于解放战争的三个战略阶段产不是很理解。然后还有一些网友想弄清楚抗日战争的三个战略阶段，泰缘号已经为你找到了相关问题的答案，接下来和我们一起看看吧，希望对大家有所帮助！</p><p><br></p><p>　　1947年5月，我国赤峰市的柴胡栏子村发生了一场震惊中央的遭遇战。</p><p><br></p><p>　　当时，我军的5名高级领导干部遭到了当地国民党残余部队和地方土匪的突袭！</p><p><br></p><p>　　面对这种情况，我军派来护送的警卫部队连长却不顾战友的性命逃之夭夭，这让革命同志遭受到了毁灭性打击。</p><p><br></p><p>　　事后，这件事报告给了中央，主席听闻警卫部队居然不顾战友的生命独自逃命。</p><p><br></p><p>　　当即勃然大怒，毛主席立即下令枪毙警卫连长和指导员。</p><p><br></p><p>　　那么，1947年5月这天，柴胡栏子村发生了什么事呢？警卫部队在战友遇难的时候为什么会临阵脱逃？</p>', '0', '', 7, '0', '0', '0', '1', '1', '0', '0', NULL, '同意', 'de', '2024-09-17 11:30:00', 'ry', '2024-09-17 22:18:31');
INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (64, 103, '在大学的学习和生活中，也需要像在军训中一样', '', 18, '', '12', '/profile/upload/2024/09/17/102_20240917205841A001.jpg', '<p>　　军训生活的描述</p><p><br></p><p>　　训练内容：详细描述军训中的各项训练，如站军姿、踢正步、跑步等。“每天清晨，站军姿成为了开启新一天的必修课。我们在烈日下挺直脊梁，感受着汗水顺着脸颊滑落，尽管双腿酸痛，但依然努力保持着标准的姿势。”</p><p><br></p><p>　　作息时间：提及严格的作息安排，强调规律生活带来的变化。“军训的作息十分规律，早睡早起让我养成了良好的生活习惯，每天都充满活力。”</p><p><br></p><p>　　教官与同学：描写教官的严格要求和悉心指导，以及同学们之间的相互鼓励与支持。“教官的严厉中蕴含着关爱，他们用自己的专业和耐心，帮助我们不断进步。而同学们在训练中互相扶持，共同克服困难，这份情谊让我倍感温暖。”</p><p><br></p><p>　　个人成长与收获</p><p><br></p><p>　　身体素质提升：阐述军训对身体的锻炼，如耐力、力量、协调性等方面的进步。“经过这段时间的军训，我的身体素质有了显著提升，不再是那个一运动就气喘吁吁的自己。”</p><p><br></p><p>　　意志品质磨砺：强调在军训中培养的坚韧不拔、吃苦耐劳、自律等品质。“面对高强度的训练和恶劣的天气，我学会了坚持，懂得了只有克服困难才能不断成长。”</p><p><br></p><p>　　团队意识增强：讲述在集体训练中体会到的团队合作的重要性。“在队列训练和拔河比赛等活动中，我深刻认识到团队的力量。只有大家齐心协力，才能取得好的成绩。”</p><p><br></p><p>　　对大学生活的启示</p><p><br></p><p>　　联系军训经历，思考如何将在军训中培养的品质运用到大学生活中。“军训让我明白，在大学的学习和生活中，也需要像在军训中一样，保持坚韧的意志、良好的团队合作精神和自律的习惯。”</p><p><br></p><p>　　表达对未来大学生活的期待和决心。“我将以军训为起点，积极面对未来的挑战，努力成为更好的自己。”</p>', '0', '', 5, '1', '0', '0', '0', '0', '0', '0', NULL, '同意', 'ks', '2024-09-17 20:58:43', 'ks', '2024-10-06 11:14:46');
INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (65, 104, '《南京大屠杀》：“遗忘大屠杀就是二次屠杀”，用生命记录中国世纪浩劫', '', 16, '办公室', '', '/profile/upload/2024/09/17/103_20240917215648A001.jpg', '<p>　　南京大屠杀，不仅是南京这座城市，更是中华民族永久难以愈合的精神创伤，也是中国人民永远不能忘却的苦难史。与犹太人在欧洲所遭受的大屠杀举世皆知不同，南京大屠杀一直鲜为人知。在《南京大屠杀》作者、美籍华裔女作家张纯如看来，这是因为南京大屠杀的受害者一直保持沉默。受害者沉默的背后，是美、英等国家对日本的纵容，他们试图替日本掩饰罪恶。张纯如详细记载了日本在南京的暴行，以及世界为保留南京大屠杀记忆作了怎样的努力和斗争。<br></p>', '0', '', 5, '1', '0', '0', '1', '1', '0', '0', NULL, '同意', 'de', '2024-09-17 21:57:48', 'ry', '2024-09-26 11:29:35');
INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (66, 104, '七七事变，又称卢沟桥事变', '', 16, '', '', '/profile/upload/2024/09/17/104_20240917220045A002.jpg', '<p>　　七七事变，又称卢沟桥事变，是日军于1937年7月7日在宛平县发动的一场侵略袭击！七七事变又标志着日本帝国主义，向中国发动了全面的侵华战争，同时也标志着中国全民族抗日的开端，由此开辟了，世界反法西斯战争的东方战场。向为保家卫国，所有牺牲的英烈英雄致敬！勿忘国耻!<br></p>', '0', '', 2, '1', '1', '0', '1', '1', '0', '0', NULL, '同意', 'de', '2024-09-17 22:00:48', 'ry', '2024-09-26 11:29:15');
INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (67, 104, '团结就是力量这首歌产生在1943年6月晋察冀边区平山县黄泥区的一个小村子', '', 16, '', '', '/profile/upload/2024/09/17/105_20240917220812A003.jpg', '<p>　　一首歌背后的故事：团结就是力量这首歌产生在1943年6月晋察冀边区平山县黄泥区的一个小村子。为了反对日寇到边区抢粮，实行“抢光、杀光、烧光”的疯狂政策，西北战地服务团深入到河北平山和山西繁峙的广大农村参加斗争。为了配合这场斗争，牧虹和卢肃同志一起在三四天左右时间里，突击创作了小型歌剧团结就是力量，最后由牧虹同志写词，卢肃同志谱曲，为该剧增加一个幕终曲，于是这首经典名曲，就这样诞生了。团结就是力量，主要强调的一个思想就是：团结起来就是力量，在困难当中要依靠自己，坚持抗战，迎接新中国的到来。相信自己，争取美好的前途。”2015年8月26日，国家新闻出版广电总局发布了“我最喜爱的十大抗战歌曲”网络投票结果，团结就是力量是入选的10首歌曲之一。</p><p><br></p><p>　　团结就是力量铸就民族之魂抗战时的日本兵有那么好打吗？资料来源：刘庭华的中国抗日战争与第二次世界大战系年要录.统计荟萃与其坐而待亡，忍辱受侮，不如保全国格，死中求生，与敌作一决战。如我再不抗战，则国民精神亦必日趋于消沉，民族生机毁灭无余矣！蒋介石日记（1937.10.31）1937-1945年，国军发动大规模会战22次，战役1117次，普通战斗28931次。4321名飞行员血洒长空，2468架战机被击落；国军海军全军覆没，104艘舰艇全部打光；国军陆军320万名官兵壮烈牺牲，206名将军殉国。根据陈钦我的河山:抗日正面战场全纪实整理而成据不完全统计，抗日战争时期，中国共产党领导的八路军、新四军及各种人民武装，在敌后战场上对敌发起主动作战，大小战斗共达12.5万次，最多时投入兵力达90多万正规军和200多万民兵，并在抗战相持阶段上升为主战场，担负起抗击一大部分侵华日军主力的作战任务。步平、荣维木中华民族抗日战争全史930多座城市被占领3500万人员死伤中日死伤比例为6:1然而，这场战争是以觉醒了的中华民族为对手的战争，不是以蒋介石政府为对手的战争，因此，即使汪精卫投降，即使蒋介石本身也动摇，但中华民族的意志是不会动摇的，中日甲午战争，日俄战争和日德战争都是以政府为对手的战争，因此，打败了敌军，就挫伤了敌方政府的战争意志，变成日本的胜利，但在以民族为对手的战争，即使在个别的战斗中战胜几百次，只要挫伤不了民族抗战的意志，就不能成为全局的胜利。</p><p><br></p><p>　　（日）井上清日本历史大公报载文说：“几千年来，中国所怀抱的观念是天下，是家族，近代西方的民族意识和国家观念，始终没有打入我们老百姓的骨髓里，直到现在，敌顽攻进来的巨炮和重弹，轰醒了我们的民族意识（今天）我们从亡国灭种的危机中，开始觉悟了中国民族的整体性和不可分性。这是民族自觉史的开端，是真正的新中国国家的序幕”。晏阳初农民抗战的发动人民的民族自觉性！东亚病夫居然奋起了，睡狮果然醒了，从前只是一大块沃土，一大盘散沙的死中国，现在是有血有肉的活中国了。这一天朱自清1939年7月7日日本的飞机炸弹让中国所有人的命运联结到了一起，不管他们相距多遥远。日本人让中国人知道，中国是整个的，这对地方观念极强的中国人是民族精神的洗礼。抗战回望抗日战争胜利的原因原因1.1.中国人民的中国人民的民族觉醒民族觉醒、民族团结民族团结和和民族抗争民族抗争。（根本原因）（根本原因）2.2.中国共产党在全民族抗战中发挥了中国共产党在全民族抗战中发挥了中流砥柱中流砥柱的的作用。</p><p><br></p><p>　　作用。3.3.国际反法西斯国家的相互支援国际反法西斯国家的相互支援。4.4.抗日战争是正义之战。抗日战争是正义之战。抗日战争胜利的伟大意义1.1.是近代以来反抗外敌入侵第一次取得的伟是近代以来反抗外敌入侵第一次取得的伟大胜利。大胜利。2.2.是中华民族由衰败到振兴的转折点。3.3.为世界反法西斯战争胜利作出重要贡献。历史就是历史，事实就是事实，任何人都不可能改变历史和事实。付出了巨大牺牲的中国人民，将坚定不移捍卫用鲜血和生命写下的历史。任何人想要否定、歪曲甚至美化侵略历史，中国人民和各国人民绝不答应！新的历史条件下，全党全国各族人民要大力弘扬伟大抗战精神，不断增强团结一心的精神纽带，自强不息的精神动力，不断以坚持和发展中国特色社会主义的新成就告慰我们的前辈和英烈！2014年习近平总书记在纪念抗战爆发77周年的讲话民族利益至上、宁死不屈的民族自尊品格。万众一心、共赴国难的民族团结意识。不畏强暴、血战到底的民族英雄气概。百折不挠、自强自立的民族自强信念。开拓创新、发展新路的民族创造精神。坚持正义、维护和平的民族奉献精神。抗战中形成的民族精神是不忘初心继续前进第第2222课课抗日战争的胜利抗日战争的胜利一、课程标准简述七七事变的史实，知道中国全民族抗战从此开始。以侵华日军南京大屠杀等罪行为例，认识日本军国主义凶恶残暴的侵略本质。探讨抗日战争胜利的历史意义。</p><p><br></p><p>　　二、教学目标：1、知识与能力：知道七七事变及四大会战的基本史实，了解全民族坚持抗战的史实；掌握抗日战争胜利的时间和历史意义，分析抗日战争胜利的原因。2、过程与方法：通过阅读史料、识图片材料结合所学知识思考抗日战争胜利的意义和原因，培养学生综合归纳、分析、表达历史问题的能力。3、情景态度和价值观：通过对抗日战争意义和胜利原因的分析，懂得正义战争终将取得最终胜利的道理，并培养铸就正确积极的民族精神。</p><p><br></p><p>　　三、教学重难点：1、重点：抗日战争胜利的历史意义。2、难点：抗日战争胜利的原因。</p><p><br></p><p>　　四、教学方法：讲授法、讨论法、图示法、自主学习法、材料解析法。</p><p><br></p><p>　　五、学情分析：八年级学生已有一年多学习历史的基础，有一定的历史基础和综合分析能力，为教师在教学上提供了较大的空间，故应在课堂上调动学生的积极性。活跃课堂氛围，以此提高学习效率。</p><p><br></p><p>　　六、教学过程：</p><p><br></p><p>　　（一）导入：通过播放歌曲团结就是力量，介绍歌曲的创作过程，提问为什么这首歌能被选进“我最喜爱的十大抗战歌曲”？歌曲的主题思想是否真实反映了抗战精神？由此导入新课。</p><p><br></p><p>　　（二）主要内容：</p><p><br></p><p>　　1、分享视频，提问：抗日战争时期的日本兵有那么好打吗？由学生回答后，展示1937年6月中日陆军师兵力比较的数据图，通过数据中展示的中日力量对比可以直观看出日军占有绝对优势，由教师分析总结：中国重工业薄弱，无法自己生产枪炮，只能靠一点点进口，像新式装备坦克更是没有，装备实力上的巨大差距让我们产生了一个巨大的疑惑：七七事变后，日本提出了三月灭亡中国论，从当时的实力对比上看，中国获胜的几率微乎其微，那这场毫无胜算的仗我们还要不要打？由学生回答，培养学生爱国情操，并进入下一问题的学习。</p><p><br></p><p>　　2、展示1937年10月蒋介石日记中的节选，由此阐述西安事变史实及西安事变后第二次国共合作，国军正面战场开始全面抵抗日军入侵。介绍正面战场的22次大会战，介绍重要的会战过程，并展示伤亡数据。提问：死伤如此巨大，为何我国民仍能够坚持抵抗？</p><p><br></p><p>　　3、展示中华民族抗日战争全史，介绍共产党敌后战场的奋战状况，然后，总体展示抗战正面敌后战场的整体状况，通过数据，让学生了解虽然国共两党合作并负隅抵抗，但死伤人数仍旧很多，从图片中让学生感受战争的惨烈，并进一步提出疑问：那么惨烈的战斗，为什么在此之前的两次鸦片战争、甲午中日战争、八国联军侵华战争，中国都会最终宣告失败而投降并签署不平等条约，而在伤亡更为惨烈的抗日战争中中国却一直在坚持抗战呢？</p><p><br></p><p>　　4、展示南京大屠杀、潘家峪惨案的史料、图片、伤亡数据，从情感上引导学生，如果你是惨案中的幸存者之一，你虽知抗战不易，也不一定最终能够取得胜利，但这场仗你会不会坚持打下去？得到学生肯定回答后，阐述人民抗战的自觉性。有了民族的觉醒，就有了全民族的抗战，通过本地博物馆里的照片，让学生思考图片反映了什么问题？利用图文介绍知识分子在抗战中的先进带领、各个阶层人民为抗战胜利做出的贡献，以此让学生体会抗日战争是全民族的抗战，并总结：由于人民是团结的、是不屈的，所以在战争持续了14年后中国最终赢得了抗日战争的胜利。</p><p><br></p><p>　　5、通过展示史料加以分析，总结抗日战争胜利的原因、意义和在抗战中形成的民族精神。</p><p><br></p><p>　　七、板书设计：第22课抗日战争的胜利一、全民族的抗战二、抗战胜利的原因三、抗战胜利的意义八、教学反思：</p><p><br></p><p>　　</p>', '0', '', 5, '1', '1', '0', '1', '1', '0', '0', NULL, '通过', 'de', '2024-09-17 22:06:30', 'ry', '2024-09-26 11:28:58');
INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (68, 104, '悲壮的抗争与辉煌的胜利：中国近现代战争的历史回顾 ', '', 16, '', '', '/profile/upload/2024/09/17/106_20240917221615A004.jpg', '<p>　　中国近现代战争的历史，是一部充满悲壮与抗争的史诗。从鸦片战争到抗日战争，中国人民在抗击外侮、争取民族独立和人民解放的伟大斗争中，付出了巨大的代价，也取得了辉煌的胜利。</p><p><br></p><p>　　1840年，鸦片战争爆发，英国以贩卖鸦片为借口，对中国发动侵略战争。清政府在腐败无能的情况下，签订了《南京条约》，使中国沦为半殖民地半封建社会。</p><p><br></p><p>　　1894年，中日甲午战争爆发。清政府在战争中的腐败和无能，导致了北洋舰队的全军覆没。战后签订的《马关条约》，使中国丧失了更多的领土和主权。</p><p><br></p><p>　　1931年，日本发动九一八事变，侵占了中国东北。1937年，卢沟桥事变爆发，日本全面侵华。在民族危亡的关头，中国人民团结一心，共赴国难，进行了长达八年的抗日战争。经过艰苦卓绝的斗争，1945年，中国人民终于赢得了抗日战争的伟大胜利。</p><p><br></p><p>　　在这些战争中，中国人民展现出了顽强的抗争精神和不屈的意志。他们用自己的血肉之躯，筑起了保卫家园的钢铁长城。他们用简陋的武器，对抗着武装到牙齿的敌人。他们用生命和鲜血，谱写了一曲曲惊天地、泣鬼神的英雄赞歌。</p><p><br></p><p>　　在这些战争中，涌现出了无数可歌可泣的英雄人物。有在鸦片战争中英勇抗击英军的关天培；有在甲午战争中为国捐躯的邓世昌；有在抗日战争中英勇牺牲的张自忠、赵登禹等。</p><p><br></p><p>　　回顾中国近现代战争的历史，我们深感和平的珍贵和不易。今天的中国，已经不再是那个任人宰割的“东亚病夫”。</p><p><br></p><p>　　但是，我们不能忘记历史，更不能忘记那些为国家独立和民族解放而英勇献身的先烈们。我们应该铭记历史，珍爱和平，发扬抗战精神，为实现中华民族伟大复兴的中国梦而努力奋斗。</p>', '0', '', 8, '1', '1', '0', '1', '1', '0', '0', NULL, '同意', 'de', '2024-09-17 22:13:12', 'ry', '2024-09-26 11:28:40');
INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (69, 104, '作为当年全球著名的驻外记者，福尔曼被称为“现代马可波罗”', '', 16, '', '', '', '<p>　　1944年6月，美国合众社、伦敦泰晤士报的著名战地记者哈里森·福尔曼突破国民党层层封锁，来到革命圣地延安。哈里森·福尔曼，美国著名摄影记者、探险家、作家和美国地理学会会员。</p><p><br></p><p>　　作为当年全球著名的驻外记者，福尔曼被称为“现代马可波罗”，早在上世纪30年代初他便来到中国西藏，他拍摄了大量关于西藏风土人情的照片。1937年抗战全面爆发后，福尔曼立即转换报道方向，他来到上海，对这里进行了广泛报道。</p>', '0', '', 9, '0', '0', '0', '1', '1', '0', '0', NULL, '同意', 'de', '2024-09-17 22:23:17', 'ry', '2024-09-17 22:27:16');
INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (70, 104, '福尔曼发现中国共产党领导的敌后抗日武装力量', '', 16, '', '', '', '<p>　　随着报道的深入，福尔曼发现中国共产党领导的敌后抗日武装力量，在抗战中发挥着重要作用，但国民党却对延安地区进行全面封锁，不允许对其进行客观报道。与此同时，因为国民党的腐败，导致国统区老百姓民怨沸腾。</p><p><br></p><p>　　跟国统区相比，中共领导的抗日根据地却蒸蒸日上，老百姓抗日热情高涨。在这样的背景下，无数外国记者都要求去延安地区进行实地采访。起初，国民党当局直接拒绝了，最终蒋介石迫于舆论压力，只能勉强同意，但严格规定了记者数量。</p>', '0', '', 11, '0', '0', '0', '1', '1', '0', '0', NULL, '同意', 'de', '2024-09-17 22:23:57', 'ry', '2024-09-17 22:27:27');
INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (71, 104, '中外记者西北参观团”抵达延安，福尔曼成为其中一员', '', 16, '', '', '/profile/upload/2024/09/17/107_20240917222610A005.jpg', '<p>　　1944年6月9日，“中外记者西北参观团”抵达延安，福尔曼成为其中一员，他们受到中共方面最为隆重的欢迎。为了让外国记者们全面了解中共情况，延安交际处组织中外记者参观了边区的机关、学校、工厂和乡村等。</p><p><br></p><p>　　当时毛主席等人指示，只要“中外记者西北参观团”提出了要求，都要尽量满足。正如此，我们看到福尔曼拍摄了大量照片，包括跟中共领导人、八路军高级将领、八路军战士、民兵等的合影。</p>', '0', '', 10, '0', '1', '0', '1', '1', '0', '0', NULL, '同意', 'de', '2024-09-17 22:25:05', 'admin', '2024-09-24 22:56:58');
